import React, {useState,useEffect}from 'react'
import './App.css';

const FetchAPI = ()=> {
  const [filled, setFilled] = useState(0);
	const [isRunning, setIsRunning] = useState(false);
  const [result, setresult] = useState(null);
  const [simulatorstatus, setsimulatorstatus] = useState(null);
  ;
 
  const BatteryOn = () => {
    document.getElementsByClassName('progressbar')[0].style.display = "block";
   
    setIsRunning(true);
   
    fetch(`https://api.thingspeak.com/update?api_key=FVXMUNKFSR3PRVRT&field2=1`).then(res => {
    
      setTimeout(() => {
        document.getElementsByClassName('progressbar')[0].style.display = "none";
        setFilled(0)
        setIsRunning(false)
        setresult("on")
        setTimeout(()=>{
          alert('Batteryon')
         },[1000])
    
      }, [39000])
     
     
    })
  }


  const BatteryOff = () =>{
    document.getElementsByClassName('progressbar')[0].style.display = "block";
   
    setIsRunning(true);
    fetch("https://api.thingspeak.com/update?api_key=FVXMUNKFSR3PRVRT&field2=0").then(res => {
    
      setTimeout(() => {
        setresult("off")
        setIsRunning(false)
        document.getElementsByClassName('progressbar')[0].style.display = "none";
        setFilled(0)
       setTimeout(()=>{
        alert('Batteryoff')
       },[1000])
      
     
      }, [39000])

    })
 

  };
  
  const SimulatorOn = () =>{
    document.getElementsByClassName('progressbar')[0].style.display = "block";
   
    setIsRunning(true);
    fetch("https://api.thingspeak.com/update?api_key=FVXMUNKFSR3PRVRT&field1=1").then(res => {
      setTimeout(() => {
        setsimulatorstatus("on")
        setIsRunning(false)
        document.getElementsByClassName('progressbar')[0].style.display = "none";
        setFilled(0)
       setTimeout(()=>{
        alert('Simulatoron')
       },[1000])
      
     
      }, [39000])
    })
 

  };

  const SimulatorOff = () =>{
    document.getElementsByClassName('progressbar')[0].style.display = "block";
   
    setIsRunning(true);
    fetch("https://api.thingspeak.com/update?api_key=FVXMUNKFSR3PRVRT&field1=0").then(res => {
      setTimeout(() => {
        setsimulatorstatus("off")
        setIsRunning(false)
        document.getElementsByClassName('progressbar')[0].style.display = "none";
        setFilled(0)
       setTimeout(()=>{
        alert('Simulatoroff')
       },[1000])
      
     
      }, [39000])
    })
 

  };


  useEffect(() => {
		if (filled < 100 && isRunning) {
			setTimeout(() => setFilled(prev => prev += 5),2000)
		}
	},[filled, isRunning])

  


  return (
    <div>
             <div className="yt-loader"></div>  
            
            <div class="center">
              
     
         <h3>Battery : {result}</h3><br/>

          <button class="button1" onClick={BatteryOn} > batteryon</button>
         
           <button class="button2" onClick={BatteryOff}> batteryoff</button>
        </div>
         <br/><br/>
         
         <div class="center">
                        
         <h3>simulator : {simulatorstatus}</h3><br/>
      

          <button  class="button1" onClick={SimulatorOn} > simulatoron</button>{''}
         
           <button  class="button2" onClick={SimulatorOff}> simulatoroff</button>
          
        </div>
         <br/><br/>
         

    </div>
  
  );
}
export default FetchAPI;
