import React, {useState,useEffect}from 'react'
import './App.css';

const FetchAPI = ()=> {
  const [filled, setFilled] = useState(0);
	const [isRunning, setIsRunning] = useState(false);
  const [result, setresult] = useState(null);
 
  ;
  const apiGet = () =>{
    fetch(`https://api.thingspeak.com/channels/1985134/fields/2.json?results=1`)
      .then((response) => response.json())
      .then((jsdata) => {
        console.log(jsdata); 

      });
  };

  const BatteryOn = () => {
    document.getElementsByClassName('progressbar')[0].style.display = "block";
   
    setIsRunning(true);
   
    fetch(`https://api.thingspeak.com/update?api_key=FVXMUNKFSR3PRVRT&field2=1`).then(res => {
    
      setTimeout(() => {
       
        alert('Batteryon')
        setFilled(0)
        setIsRunning(false)
        setresult("on")
      }, [2000])
      document.getElementsByClassName('progressbar')[0].style.display = "none";
     
    })
  }


  const BatteryOff = () =>{
    document.getElementsByClassName('progressbar')[0].style.display = "block";
   
    setIsRunning(true);
    fetch("https://api.thingspeak.com/update?api_key=FVXMUNKFSR3PRVRT&field2=0").then(res => {
    
      setTimeout(() => {
        alert('Batteryoff')
      }, [2000])
      document.getElementsByClassName('progressbar')[0].style.display = "none";
      setFilled(0)
      setIsRunning(false)
    })
 

  };

  


  return (
    <div>
            <div class="center">
         <h3>Battery : {result}</h3><br/>

          <button class="button1" onClick={BatteryOn} > batteryon</button>
         
           <button class="button2" onClick={BatteryOff}> batteryoff</button>
        </div>
         <br/><br/>
         

    </div>
  
  );
}
export default FetchAPI;
