import React, {useState,useEffect}from 'react'
import './App.css';

const FetchAPI = ()=> {
  const [filled, setFilled] = useState(0);
	const [isRunning, setIsRunning] = useState(false);
  const [result, setresult] = useState(null);
 
  ;
 
  const BatteryOn = () => {
    document.getElementsByClassName('progressbar')[0].style.display = "block";
   
    setIsRunning(true);
   
    fetch(`https://api.thingspeak.com/update?api_key=FVXMUNKFSR3PRVRT&field2=1`).then(res => {
    
      setTimeout(() => {
       
        alert('Batteryon')
        setFilled(0)
        setIsRunning(false)
        setresult("on")
      }, [2000])
      document.getElementsByClassName('progressbar')[0].style.display = "none";
     
    })
  }


  const BatteryOff = () =>{
    document.getElementsByClassName('progressbar')[0].style.display = "block";
   
    setIsRunning(true);
    fetch("https://api.thingspeak.com/update?api_key=FVXMUNKFSR3PRVRT&field2=0").then(res => {
    
      setTimeout(() => {
        alert('Batteryoff')
      }, [2000])
      document.getElementsByClassName('progressbar')[0].style.display = "none";
      setFilled(0)
      setIsRunning(false)
    })
 

  };

  useEffect(() => {
		if (filled < 100 && isRunning) {
			setTimeout(() => setFilled(prev => prev += 5), 50)
		}
	},[filled, isRunning])

  


  return (
    <div>
            <div class="center">
        <div className="progressbar">
			  <div style={{
				  height: "100%",
				  width: `${filled}%`,
				  backgroundColor: "blue",
				  transition:"width 0.5s"
			  }}></div>
        <span className="progressPercent" >{ filled }%</span></div>
         <h3>Battery : {result}</h3><br/>

          <button class="button1" onClick={BatteryOn} > batteryon</button>
         
           <button class="button2" onClick={BatteryOff}> batteryoff</button>
        </div>
         <br/><br/>
         
         <div class="center">
         <h3>simulator</h3><br/>

          <button  class="button1" onClick={SimulatorOn} > simulatoron</button>{''}
         
           <button onClick={SimulatorOff}> simulatoroff</button>
          
        </div>
         <br/><br/>
         

    </div>
  
  );
}
export default FetchAPI;
